import React from 'react';
import { Chart } from 'react-google-charts';


function CountryChart({data}) {
    const chartData = [['Country', 'Intensity', 'Likelihood', 'Relevance']];
  
    data.slice(0,10).forEach(item => {
      const {
        intensity,
        likelihood,
        relevance,
        country,
      } = item;
  
      chartData.push([country, intensity, likelihood, relevance]);
    });

    return(
        <Chart
        width={'100%'}
        height={'400px'}
        chartType="ColumnChart"
        loader={<div>Loading Chart</div>}
        data={chartData}
        options={{
            title: 'Country Data',
            hAxis: { title: 'Country',},
            vAxis: {title: 'Value'},
            seriesType :"bars",
            // series: {
            //     1: { axis: 'CategoryAxis' },
            //   },
            //   axes: {
            //     y: {
            //       CategoryAxis: {
            //         // Customize the options for the secondary category axis here
            //         textStyle: {
            //           fontSize: 12, // Adjust the font size
            //         },
            //         titleTextStyle: {
            //           fontSize: 14, // Adjust the title font size
            //           color: 'red', // Change the title color
            //         },
            //         gridlines: {
            //           color: 'lightgray', // Change the gridlines color
            //           count: 5, // Adjust the number of gridlines
            //         },
            //         // Add any other customizations you need
            //       },
            //     },
            //   },
            }}
          />
    )

}

export default CountryChart;
