import { useState, useEffect, useRef } from "react";
import { getData } from "../service/api";
import { Chart } from "react-google-charts";
import {Table, TableHead, TableBody, TableRow, TableCell, Typography } from "@mui/material";

function Dashboard() {
    const [data, setData] = useState([]);
    const chartData = [['Country', 'Intensity','Linkelihood','Relevance']];
    // const [chartData, setChartData] = useState({});
    // const [chartOptions, setChartOptions] = useState({})

        const inValue = [6, 6, 6, 6, 6, 6, 60, 16, 16, 6];
        const titles = ['United States of America', 'United States of America', 'United States of America', 'Mexico', '', '', '', '', 'Nigeria', 'Lebanon'];
        const info = {
            "chartType":"ColumnChart",
            "chart": [
            ["country","intensity"],
            ["United States of America",6],
            ["United States of America",6],
            ["Mexico",6],
            ["Nigeria",60],
            ["Lebanon",6],
        ],
        "options": {
            "title":"Intensity"
        },
        "width": "100%",
        "height": "400px"
    }
        // const chartData = {
        //   labels: titles,
        //   datasets: [
        //     {
        //       label: 'Intensity',
        //       data: inValue,
        //       backgroundColor: 'rgba(75, 192, 192, 0.2)', // Customize the color as needed
        //       borderColor: 'rgba(75, 192, 192, 1)', // Customize the color as needed
        //       borderWidth: 1,
        //     },
        //   ],
        // };
      
        // const chartOptions = {
        //   scales: {
        //     y: {
        //       beginAtZero: true,
        //       title: {
        //         display: true,
        //         text: 'Intensity',
        //       },
        //     },
        //     x: {
        //       title: {
        //         display: true,
        //         text: 'Title',
        //       },
        //     },
        //   },
        // }

    
    const getDetails = async() => {
        try {
        const response = await getData();
        console.log(response,"response from server")
         setData(response?.data);
    }
    catch(err) {
      console.log(err,"err in api")
    }
    }

    useEffect(() => {
        getDetails()
    },[])

    useEffect(() => {
      data?.slice(0,4).forEach((item) => {
        const {intensity, likelihood,relevance,country} = item;
        chartData.push([country, Number(intensity),Number(likelihood),Number(relevance),]);
      })
    },[data])

    useEffect(()=> {
        chartData.forEach((ele) => {
            console.log(typeof(ele),"type ele")
        })
    },[chartData])

    return(
        <div>
            <Typography variant="h5">Intensity Chart</Typography>
            <div>
                {/* <Chart data = {chartData} chartType="ColumnChart"/> */}
            {data? <Chart
                width={'100%'}
                height={'400px'}
                chartType="BarChart"
                loader={<div>Loading Chart</div>}
                data={chartData}
                options={{
                    title: 'Data Chart',
                    hAxis: { title: 'Country',},
                    vAxis: {title: 'Value'},
                    seriesType :"bars",
                    // series: {
                    //     1: { axis: 'CategoryAxis' },
                    //   },
                    //   axes: {
                    //     y: {
                    //       CategoryAxis: {
                    //         // Customize the options for the secondary category axis here
                    //         textStyle: {
                    //           fontSize: 12, // Adjust the font size
                    //         },
                    //         titleTextStyle: {
                    //           fontSize: 14, // Adjust the title font size
                    //           color: 'red', // Change the title color
                    //         },
                    //         gridlines: {
                    //           color: 'lightgray', // Change the gridlines color
                    //           count: 5, // Adjust the number of gridlines
                    //         },
                    //         // Add any other customizations you need
                    //       },
                    //     },
                    //   },
                    }}
                  />: <></> }
          </div>
            {/* <Table>
              <TableHead>
                 <TableRow>
                    <TableCell>Intensity</TableCell>
                    <TableCell>Likelihood</TableCell>
                    <TableCell>Relevance</TableCell>
                    <TableCell>Year</TableCell>
                    <TableCell>Country</TableCell>
                    <TableCell>Topics</TableCell>
                    <TableCell>Region</TableCell>
                    <TableCell>City</TableCell>
                 </TableRow>
              </TableHead>
            </Table> */}
        </div>
    )
}

export default Dashboard;
