import { useState, useEffect } from "react";
import { getData } from "../service/api";
import { Bar } from 'react-chartjs-2';
import {Table, TableHead, TableBody, TableRow, TableCell, Typography } from "@mui/material";

function Dashboard() {
    // const [data, setData] = useState([]);
    const [chartData, setChartData] = useState({});
    const [chartOptions, setChartOptions] = useState({})

    useEffect(() => {
     getDetails();
    },[])

    
    const getDetails = async() => {
        try {
        const response = await getData();
        console.log(response,"response from server")
        const data = response?.data
        console.log(data,"data from api")
        if(data.length > 0){
        const intensityValues = data?.slice(0,10)
        const inValue = intensityValues.map(item => item.intensity)
        const titleInfo = data?.slice(0,10)
        const titles = titleInfo.map(item => item.title);
        console.log(intensityValues,inValue,titles,"intensity val")
      
        setChartData({
          labels: titles,
          datasets: [
            {
              label: 'Intensity',
              data: inValue,
              backgroundColor: 'rgba(75, 192, 192, 0.2)', // Customize the color as needed
              borderColor: 'rgba(75, 192, 192, 1)', // Customize the color as needed
              borderWidth: 1,
            },
          ],
        });
      
        setChartOptions({
          scales: {
            y: {
              beginAtZero: true,
              title: {
                display: true,
                text: 'Intensity',
              },
            },
            x: {
              title: {
                display: true,
                text: 'Title',
              },
            },
          },
        });
    }
    }
    catch(err) {
      console.log(err,"err in api")
    }
    }

    return(
        <div>
            <Typography variant="h5">Intensity Chart</Typography>
            <div>
               <Bar data={chartData} options={chartOptions} />
          </div>
            {/* <Table>
              <TableHead>
                 <TableRow>
                    <TableCell>Intensity</TableCell>
                    <TableCell>Likelihood</TableCell>
                    <TableCell>Relevance</TableCell>
                    <TableCell>Year</TableCell>
                    <TableCell>Country</TableCell>
                    <TableCell>Topics</TableCell>
                    <TableCell>Region</TableCell>
                    <TableCell>City</TableCell>
                 </TableRow>
              </TableHead>
            </Table> */}
        </div>
    )
}

export default Dashboard;
